mapper.select:
	SELECT CASE WHEN COUNT(0) > 0 THEN 'Y' ELSE 'N' END CHK
	  FROM DM_D_MEMBER
     WHERE MEM_ID = #{MEM_ID}

	SELECT CASE WHEN COUNT(0) > 0 THEN 'Y' ELSE 'N' END DUP
	  FROM CM_CAMPAIGN_TESTER
     WHERE MEM_ID = #{MEM_ID}

mapper.update:
	UPDATE CM_CAMPAIGN_TESTER
	   SET USE_YN    = #{USE_YN}
	      ,UPDATE_ID = #{UPDATE_ID}
	      ,UPDATE_DT = SYSDATE
     WHERE MEM_ID    = #{MEM_ID}
